package me.underlow.advent2022.day06

import me.underlow.advent2022.checkResult

fun solve1(chiper: String): Int {
    for (i in 0 until chiper.length - 4) {
        if (setOf(chiper[i], chiper[i + 1], chiper[i + 2], chiper[i + 3]).size == 4) {
            return (i + 3 + 1) // <- that's the answer
        }
    }
    return -1
}

fun solve2(chiper: String): Int {
    for (i in 0 until chiper.length - 14) {
        val set = mutableSetOf<Char>()
        for (ii in 0..13) {
            set.add(chiper[i + ii])
        }
        if (set.size == 14) {
            return (i + 13 + 1) // <- that's the answer
        }
    }
    return -1
}

fun main() {
    println(solve1("mjqjpqmgbljsphdztnvjfqwrcgsmlb")) // 7
    println(solve1("bvwbjplbgvbhsrlpgdmjqwftvncz")) // 5
    println(solve1("nppdvjthqldpwncqszvftbrmjlhg"))//6
    println(solve1("nznrnfrfntjfmvfwmzdfjlvtqnbhcprsg")) // 10
    println(solve1("zcfzfwzzqfrljwzlrfnpqdbhtmscgvjw")) //11
    println()
    val solve1 = solve1(chiper)
    println(solve1)
    val solve2 = solve2(chiper)
    println(solve2)

    checkResult(solve1, 1578)
    checkResult(solve2, 2178)
}

private val chiper = """
    
""".trimIndent()
